<div ng-controller="MainController">

  <div class="flip-book-container">

    <div class="page-container left">
      <div class="page-content" [(ngModel)]="currPage" ng-bind="currPage - 2">
      </div>
    </div>

    <div class="page-container current left" ng-class="{turn: flippingLeft}" ng-click="flipPageLeft()">
      <div class="page-content left-behind" ng-bind="currPage - 1">
      </div>
      <div class="page-content" ng-bind="currPage">
      </div>
    </div>

    <div class="page-container current right" ng-class="{turn: flippingRight}" ng-click="flipPageRight()">
      <div class="page-content" ng-bind="currPage + 1">
      </div>
      <div class="page-content right-behind" ng-bind="currPage + 2">
      </div>
    </div>

    <div class="page-container right">
      <div class="page-content" ng-bind="currPage + 3">
      </div>
    </div>

  </div>

</div>
