import { ChangeDetectorRef, Component, Directive, ElementRef, Input, ViewChild } from '@angular/core';
import { typeLayout } from '../constants/layout-variable';
import { timeout } from 'rxjs';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss']
})

export class BookComponent{
public tipo:number = typeLayout.tipo;
public currPage:number = 1;
public pageFlipDuration:number = 500;
public flippingLeft:boolean = false;
public flippingRight:boolean = false;

constructor(){

}

flipPageLeft():void {
  this.flippingLeft = true;

}


flipPageRight():void {
  this.flippingRight = true;
}

getPrev(){
  if (this.tipo==0){
    this.tipo = 3;
  } else {
    this.tipo = this.tipo -1;
  }
  this.flipPageLeft();
}

getNext(){
  if (this.tipo>=3){
    this.tipo = 0;
  } else {
    this.tipo = this.tipo +1;
  }
  this.flipPageRight();
}

}
<div class="layout-portada size-book">
  <div id="book" class="carousel border border-light-subtle shadow-lg h-100" [ngClass]="{'w-50': tipo==0, 'size-page': tipo!=0}">
    <div class="carousel-inner h-100 overflow-y-auto page-page">
      <div class="carousel-item active h-100 left">
        <app-home></app-home>
      </div>
      <div class="carousel-item h-100 left"  >
        <app-detalle ></app-detalle>
      </div>
      <div class="carousel-item h-100 right">
        <app-onepage></app-onepage>
      </div>
      <div class="carousel-item h-100 left">
        <app-onepage></app-onepage>
      </div>
    </div>
    <button class="carousel-control-prev page leftbtn" *ngIf="tipo!=0" type="button" data-bs-target="#book" data-bs-slide="prev" (click)="getPrev()" >
      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Previous</span>
    </button>
    <button class="carousel-control-next page rightbtn" type="button" data-bs-target="#book" data-bs-slide="next" (click)="getNext()">
      <span class="carousel-control-next-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Next</span>
    </button>
  </div>
</div>
