import { ChangeDetectorRef, Component, Directive, ElementRef, Input, ViewChild } from '@angular/core';
import { typeLayout } from '../constants/layout-variable';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss']
})

export class BookComponent{
public tipo:number = typeLayout.tipo;
constructor(){

}


getPrev(){
  if (this.tipo==0){
    this.tipo = 3;
  } else {
    this.tipo = this.tipo -1;
  }
  console.log(this.tipo);
}

getNext(){
  if (this.tipo>=3){
    this.tipo = 0;
  } else {
    this.tipo = this.tipo +1;
  }
  console.log(this.tipo);
}

}
<div class="layout-portada size-book">
  <div id="book" class="carousel border border-light-subtle shadow-lg h-100" [ngClass]="{'w-50': tipo==0, 'size-page': tipo!=0}">
    <div class="carousel-inner h-100 overflow-y-auto">
      <div class="carousel-item active h-100 ">
        <app-home></app-home>
      </div>
      <div class="carousel-item h-100">
        <app-detalle ></app-detalle>
      </div>
      <div class="carousel-item h-100" >
        <app-onepage></app-onepage>
      </div>
      <div class="carousel-item h-100" >
        <app-onepage></app-onepage>
      </div>
    </div>
    <button class="carousel-control-prev bg-info page" *ngIf="tipo!=0" type="button" data-bs-target="#book" data-bs-slide="prev" (click)="getPrev()" >
      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Previous</span>
    </button>
    <button class="carousel-control-next bg-info page pagea flipped" type="button" data-bs-target="#book" data-bs-slide="next" (click)="getNext()">
      <span class="carousel-control-next-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Next</span>
    </button>
  </div>
</div>


<div class="container w-100">
  <h3>Click on pages to flip</h3>
  <div class="book">
  <input type="checkbox" name="page1" id="page1">
  <input type="checkbox" name="page2" id="page2">
  <input type="checkbox" name="page3" id="page3">
  <input type="checkbox" name="page4" id="page4">
  <div class="pages">
    <div class="page1">
      <label for="page1"></label>
    </div>
    <div class="page2">
      <label for="page2"></label>
    </div>
    <div class="page3">
      <label for="page3"></label>
    </div>
    <div class="page4">
      <label for="page4"></label>
    </div>
  </div>
</div>
</div>
