import { Component, Input } from '@angular/core';
import { typeLayout } from '../constants/layout-variable';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss']
})
export class BookComponent  {
  public layout:any;
mostrar: any;
@Input() mostrar2?: any;
  constructor(){
    this.layout = typeLayout.tipo;
  }


}
<div class="layout-portada h-100 w-100">
  <div id="book" class="carousel slide size-book" [ngClass]="{'w-50': layout == 0, 'w-100': layout !=0}">
    <div class="carousel-inner h-100">
      <div class="carousel-item active h-100" [mostrar2]='mostrar'>
        <app-home></app-home>
      </div>
      <div class="carousel-item h-100" *ngIf="layout==1">
        <app-detalle></app-detalle>
      </div>
      <div class="carousel-item h-100">
        <app-onepage></app-onepage>
      </div>
    </div>
    <button class="carousel-control-prev bg-dark-subtle page" type="button" data-bs-target="#book" data-bs-slide="prev">
      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Previous</span>
    </button>
    <button class="carousel-control-next bg-dark-subtle page" type="button" data-bs-target="#book" data-bs-slide="next">
      <span class="carousel-control-next-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Next</span>
    </button>
  </div>
</div>
