import { ChangeDetectorRef, Component, Directive, ElementRef, Input, ViewChild } from '@angular/core';
import { typeLayout } from '../constants/layout-variable';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss']
})

export class BookComponent{
public tipo:number = typeLayout.tipo;
constructor(){

}


getPrev(){
  if (this.tipo==0){
    this.tipo = 3;
  } else {
    this.tipo = this.tipo -1;
  }
  console.log(this.tipo);
}

getNext(){
  if (this.tipo>=3){
    this.tipo = 0;
  } else {
    this.tipo = this.tipo +1;
  }
  console.log(this.tipo);
}

}
<div class="shadow-lg layout-portada w-100 h-100 ">
  <div id="book" class="carousel border border-light-subtle h-100 overflow-y-auto" [ngClass]="{'w-50': tipo==0, 'size-page': tipo!=0}">
    <div class="carousel-inner h-100 bg-secondary-subtle overflow-y-auto">
      <div class="carousel-item active h-100 ">
        <app-home></app-home>
      </div>
      <div class="carousel-item h-100">
        <app-detalle ></app-detalle>
      </div>
      <div class="carousel-item h-100" >
        <app-onepage></app-onepage>
      </div>
      <div class="carousel-item h-100" >
        <app-onepage></app-onepage>
      </div>
    </div>
    <button class="carousel-control-prev bg-info page" type="button" data-bs-target="#book" data-bs-slide="prev" (click)="getPrev()" >
      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Previous</span>
    </button>
    <button class="carousel-control-next bg-info page" type="button" data-bs-target="#book" data-bs-slide="next" (click)="getNext()">
      <span class="carousel-control-next-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Next</span>
    </button>
  </div>
</div>
