import { Component, Directive, Input } from '@angular/core';
import { typeLayout } from '../constants/layout-variable';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss']
})

export class BookComponent {
[x: string]: any;
  public layout:number = 0;

  constructor(){
    this.layout = typeLayout.tipo;
  }

  getPageMinus():void{
    this.layout =- 1;
    console.log(this.layout);
  }

  getPagePlus():void{
    this.layout =+ 1;
    console.log(this.layout);
  }

}
<ng-template ngFor let-hero let-i="index">
  <li>Hero number {{i}}
</ng-template>
<div class="layout-portada h-100 w-100">
  <div id="book" class="carousel slide size-book" [ngClass]="{'w-50': layout == 0, 'w-100': layout !=0}">
    <div class="carousel-inner h-100">
      <div class="carousel-item active h-100" layout=0>
        <app-home></app-home>
      </div>
      <ng-template [ngIf]="true">
        <div class="carousel-item h-100" layout=1>
          <app-detalle></app-detalle>
        </div>
      </ng-template>

      <div class="carousel-item h-100" layout=1>
        <app-onepage></app-onepage>
      </div>
    </div>
    <button class="carousel-control-prev bg-dark-subtle page" type="button" data-bs-target="#book" data-bs-slide="prev" (click)="getPageMinus()">
      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Previous</span>
    </button>
    <button class="carousel-control-next bg-dark-subtle page" type="button" data-bs-target="#book" data-bs-slide="next" (click)="getPagePlus()">
      <span class="carousel-control-next-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Next</span>
    </button>
  </div>
</div>
