<div ng-controller="MainController">

  <div class="flip-book-container">

    <div class="page-container left">
      <div class="page-content" >
      </div>
    </div>

    <div class="page-container current left" ngClass="{turn: flippingLeft}" (click)="flipPageLeft()">
      <div class="page-content left-behind" >
      </div>
      <div class="page-content" [(ngModel)]="currPage">
      </div>
    </div>

    <div class="page-container current right" ngClass="{turn: flippingRight}" (click)="flipPageRight()">
      <div class="page-content" [(ngModel)]="currPage">
      </div>
      <div class="page-content right-behind" [(ngModel)]="currPage">
      </div>
    </div>

    <div class="page-container right">
      <div class="page-content" [(ngModel)]="currPage">
      </div>
    </div>

  </div>

</div>
